websocket.functions:
// Functions defined at the top of the page
// subscribeToBoardUpdates - see Pikku Function Definitions section

// Connection handlers
export const onConnect = pikkuChannelConnectionFunc(
  async ({ logger, channel }) => {
    const { boardId } = channel.openingData
    logger.info(`Connected to board updates channel for board: ${boardId}`)
    channel.send({ type: 'welcome', message: `Connected to board ${boardId} collaboration` })
  }
)

export const onDisconnect = pikkuChannelDisconnectionFunc(
  async ({ logger, channel }) => {
    const { boardId } = channel.openingData
    logger.info(`Disconnected from board updates channel for board: ${boardId}`)
  }
)

websocket.wiring:
addChannel({
  // The channel name, used to identify the channel with types in the client
  // and needs to be unique across all channels
  name: 'board-updates',

  // The route to use for the channel. Supports parameterized routes
  // for board-specific channels
  route: '/board/:boardId',

  // Called when a client connects to the channel
  onConnect,

  // Called when a client disconnects from the channel
  onDisconnect,

  // This is a global permission that applies to all message routes,
  // unless overridden by the specific route
  auth: true,

  // Message routing configuration for different actions
  onMessageRoute: {
    action: {
      // Subscribe to board updates - permissions are handled in the function definition
      subscribe: subscribeToBoardUpdates,
    },
  },

  // Tags for documentation and organization
  tags: ['boards', 'cards', 'realtime'],
})

websocket.client:
import { PikkuWebSocket } from './pikku-websocket.gen.js'

const websocket = new PikkuWebSocket<'board-updates'>(serverUrl, apiKey)

websocket.ws.onopen = async () => {
  console.log('Connected to board collaboration')

  // Subscribe to board update messages
  websocket.subscribe((boardUpdate) => {
    console.log(`Board event: ${boardUpdate.type}`) 
    if (boardUpdate.type === 'cardCreated') {
      console.log(`New card added: ${boardUpdate.cardId}`)
    }
  })

  // Subscribe to specific board
  websocket.sendMessage('subscribe', {
    boardId: 'board-123'
  })
}

sse.functions:
/** Stream live board changes to connected clients */
export const streamBoardChanges = pikkuSessionlessFunc<
  { boardId: string },
  { type: 'cardCreated' | 'cardMoved' | 'cardUpdated'; boardId: string; cardId?: string; timestamp: number } | void
>(async ({ boardService, eventHub, channel }, { boardId }) => {
  if (!channel) {
    throw new Error('This function requires a streaming connection.')
  }

  // Send initial board status
  const board = await boardService.getBoard(boardId)
  channel.send({ 
    type: 'boardStatus', 
    boardId, 
    cardCount: board.lists.reduce((sum, list) => sum + list.cards.length, 0),
    timestamp: Date.now() 
  })

  // Subscribe to board events and stream them
  const eventHandler = (event: any) => {
    channel.send({
      type: event.type,
      boardId: event.boardId,
      cardId: event.cardId,
      timestamp: Date.now()
    })
  }

  await eventHub?.subscribe(`board:${boardId}`, eventHandler)

  // Clean up after 5 minutes
  setTimeout(() => {
    eventHub?.unsubscribe(`board:${boardId}`, eventHandler)
    channel.close()
  }, 300000)
})

sse.wiring:
addHTTPRoute({
  auth: true,
  method: 'get',
  route: '/board/:boardId/stream',
  func: streamBoardChanges,
  sse: true,
  docs: {
    description: 'Stream real-time board changes and updates',
    tags: ['boards', 'streaming', 'realtime'],
  },
})

