rpc.functions:
// RPC function that demonstrates recursive calls and depth tracking
export const rpcTest = pikkuSessionlessFunc<{ in: number }>(
  async ({ logger, rpc }, data) => {
    logger.debug(`RPC Test with RPC: ${rpc?.depth}`)
    if (rpc?.depth && rpc?.depth < 10) {
      data.in += 1
      rpc.invoke('rpcTest', data)
      await rpc.invoke(`rpcTest`, data)
    }
    return data
  }
)

rpc.wiring:
// RPC functions are automatically discovered by Pikku!
// No explicit wiring configuration needed.
//
// How it works:
// 1. Export any pikkuFunc or pikkuSessionlessFunc
// 2. Pikku scans your source files during build
// 3. Functions become available via rpc.invoke('functionName', data)
// 4. Type-safe RPC clients are auto-generated
//
// That's it! No addRPCRoute() or registration required.

rpc.client:
import { PikkuRPCClient } from './pikku-rpc.gen.js'

const rpcClient = new PikkuRPCClient({
  serverUrl: 'http://localhost:3000/rpc'
})

// Type-safe RPC call
const result = await rpcClient.invoke('rpcTest', {
  in: 1
})

console.log(`Final result: ${result.in}`)
console.log(`RPC recursion completed successfully`)

mcp.functions:
// MCP Tool: AI can call this to create cards
export const createCardTool = pikkuMCPToolFunc<CreateCard>(
  async ({ rpc, logger }, data) => {
    const card = await rpc.invoke('createCard', data)

    return [{
      type: 'text',
      text: `Successfully created card "${data.title}" with ID: ${card.cardId}`,
    }]
  }
)

// MCP Resource: AI can read board information
export const getBoardResource = pikkuMCPResourceFunc<{ boardId: string }>(
  async ({ boardService }, { boardId }) => {
    const board = await boardService.getBoard(boardId)

    return [{
      uri: `board/${boardId}`,
      text: JSON.stringify({
        title: board.title,
        listCount: board.lists.length,
        totalCards: board.lists.reduce((sum, list) => sum + list.cards.length, 0)
      }),
    }]
  }
)

// MCP Prompt: Kanban assistant template
export const kanbanAssistantPrompt = pikkuMCPPromptFunc<unknown>(async () => {
  return [{
    role: 'user',
    content: {
      type: 'text',
      text: `You are a Kanban board assistant helping teams organize their work. Your goal is to:

1. Help create and organize cards across different lists (To Do, In Progress, Done)
2. Suggest improvements to workflow and card organization
3. Track progress and identify bottlenecks
4. Generate status reports and insights

Focus on productivity and clear task management.`,
    },
  }]
})

mcp.wiring:
addMCPTool({
  name: 'createCard',
  description: 'Create new cards in Kanban board lists',
  func: createCardTool,
  tags: ['kanban', 'cards', 'productivity'],
})

addMCPResource({
  uri: 'board/{boardId}',
  title: 'Board Information',
  description: 'Get board overview with list and card counts',
  func: getBoardResource,
  tags: ['kanban', 'boards', 'overview'],
})

addMCPPrompt({
  name: 'kanbanAssistant',
  description: 'Generate Kanban assistant prompts for productivity help',
  func: kanbanAssistantPrompt,
  tags: ['kanban', 'assistant', 'productivity'],
})
